import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Home from "./pages/Home";
import RecipesPage from "./pages/RecipesPage";
import RecipePage from "./pages/RecipePage";
import AddRecipePage from "./pages/AddRecipePage";
import EditRecipePage from "./pages/EditRecipePage";
import Navbar from "./components/Navbar"; // Make sure to import Navbar

const App = () => {
  // Check if user is logged in by checking the token in localStorage
  const isLoggedIn = Boolean(localStorage.getItem("token"));

  return (
    <Router>
      {/* Pass isLoggedIn to Navbar to manage visibility of "My Recipes" link */}
      <Navbar isLoggedIn={isLoggedIn} />

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/my-recipes" element={<RecipesPage />} />{" "}
        {/* This is the "My Recipes" page */}
        <Route path="/recipe/:id" element={<RecipePage />} />
        <Route path="/add-recipe" element={<AddRecipePage />} />
        <Route path="/edit-recipe/:id" element={<EditRecipePage />} />
      </Routes>
    </Router>
  );
};

export default App;
