import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import Navbar from "../components/Navbar";

const HomeContainer = styled.div`
  padding: 0;
  text-align: center;
  overflow-x: hidden;
  background-color: #faf3e0;
`;

const WelcomeBanner = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 50vh;
  background-color: #faf3e0;
  padding: 20px;
  color: #333333;
  text-align: center;
  margin-top: 80px;
`;

const WelcomeMessage = styled.h2`
  margin-bottom: 10px;
  font-size: 2.5rem;
  font-weight: bold;
  color: #6a4c41;

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const Description = styled.p`
  font-size: 1.2rem;
  margin-bottom: 15px;
  color: #6a4c41;
  max-width: 800px;
  margin: 0 auto;

  @media (max-width: 768px) {
    font-size: 1rem;
  }
`;

const CallToAction = styled.div`
  margin: 30px 0;
`;

const CTAButton = styled(Link)`
  padding: 12px 24px;
  background-color: #ff6b6b;
  color: white;
  text-decoration: none;
  border-radius: 25px;
  margin: 0 10px;
  font-size: 16px;
  font-weight: bold;
  transition: background-color 0.3s ease, transform 0.3s ease;

  &:hover {
    background-color: #ff4757;
    transform: translateY(-2px);
  }

  @media (max-width: 768px) {
    margin: 10px 0;
    font-size: 14px;
  }
`;

const Home = ({ isLoggedIn }) => {
  return (
    <HomeContainer>
      <Navbar isLoggedIn={isLoggedIn} />
      <WelcomeBanner>
        <WelcomeMessage>Welcome to Recipe Mania!</WelcomeMessage>
        <Description>
          Save and manage your favorite recipes effortlessly.
        </Description>
        <CallToAction>
          <CTAButton to="/register">Sign Up</CTAButton>
          <CTAButton to="/login">Login</CTAButton>
        </CallToAction>
        {isLoggedIn && (
          <CTAButton to="/my-recipes">Go to My Recipes</CTAButton> // New button to go to Recipes Page
        )}
      </WelcomeBanner>
    </HomeContainer>
  );
};

export default Home;
