import D1 from "../src/components/Image/Pancakes.jpg";
[{
  id: 1,
  name: "Delicious Pancakes",
  image: D1,
  ingredients: "Flour, Milk, Eggs, Baking Powder, Sugar, Salt, Butter",
  instructions: "1. Mix dry ingredients. 2. Add wet ingredients. 3. Cook on a hot griddle."
}, {
  id: 2,
  name: "Spaghetti Carbonara",
  image: "sample-recipe2.jpg",
  ingredients: "Spaghetti, Eggs, Parmesan Cheese, Pancetta, Black Pepper",
  instructions: "1. Cook spaghetti. 2. Mix eggs and cheese. 3. Combine with cooked spaghetti and pancetta."
}, {
  id: 3,
  name: "Paneer Butter Masala",
  image: "paneer-butter-masala.jpg",
  ingredients: "Paneer, Butter, Tomato, Cream, Onion, Garlic, Ginger, Spices",
  instructions: "1. Sauté onions, garlic, and ginger. 2. Add tomatoes and spices. 3. Stir in paneer and cream. 4. Simmer until well-cooked."
}, {
  id: 4,
  name: "Samosa",
  image: "samosa.jpg",
  ingredients: "Potatoes, Peas, Spices, Flour, Oil",
  instructions: "1. Prepare filling with potatoes and peas. 2. Make dough and shape into triangles. 3. Fill and seal samosas. 4. Fry or bake until golden brown."
}, {
  id: 5,
  name: "Brownies",
  image: "brownies.jpg",
  ingredients: "Flour, Cocoa Powder, Sugar, Butter, Eggs, Vanilla Extract",
  instructions: "1. Mix dry ingredients. 2. Combine with melted butter and eggs. 3. Bake until set and a toothpick comes out with moist crumbs."
}, {
  id: 6,
  name: "Khaman",
  image: "khaman.jpg",
  ingredients: "Besan (Chickpea Flour), Yogurt, Lemon Juice, Mustard Seeds, Green Chilies, Turmeric, Baking Soda, Sugar",
  instructions: "1. Mix besan with yogurt, lemon juice, and spices. 2. Pour into a greased tray and steam for about 15-20 minutes. 3. Temper with mustard seeds and green chilies. 4. Cut into pieces and serve."
}];
