import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import recipesData from "../data"; // Import the static data

const PageContainer = styled.div`
  padding: 20px;
  max-width: 800px;
  margin: 0 auto;
`;

const RecipeImage = styled.img`
  width: 100%;
  height: 300px; /* Adjust as needed */
  object-fit: cover;
  border-bottom: 1px solid #ddd;
  margin-bottom: 20px;
  border-radius: 8px; /* Optional: for rounded corners */
`;

const RecipeTitle = styled.h1`
  margin-bottom: 20px;
  font-size: 2rem; /* Adjust as needed */
  color: #333;
`;

const Ingredients = styled.div`
  margin-bottom: 20px;
`;

const Preparation = styled.div``;

const IngredientsTitle = styled.h2`
  font-size: 1.5rem;
  color: #555;
  margin-bottom: 10px;
`;

const PreparationTitle = styled.h2`
  font-size: 1.5rem;
  color: #555;
  margin-bottom: 10px;
`;

const RecipePage = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRecipe = async () => {
      setLoading(true);

      // Check if the recipe ID matches a dummy recipe
      const dummyRecipe = recipesData.find(
        (recipe) => recipe.id === parseInt(id)
      );

      if (dummyRecipe) {
        setRecipe(dummyRecipe);
        setLoading(false);
        return;
      }

      try {
        // Fetch the recipe from the API
        const response = await fetch(`/api/recipes/${id}`);
        if (!response.ok) {
          throw new Error("Recipe not found");
        }
        const data = await response.json();
        setRecipe(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipe();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!recipe) {
    return <div>Recipe not found</div>;
  }

  return (
    <PageContainer>
      <RecipeImage src={recipe.image} alt={recipe.name} />
      <RecipeTitle>{recipe.name}</RecipeTitle>
      <Ingredients>
        <IngredientsTitle>Ingredients</IngredientsTitle>
        <p>{recipe.ingredients}</p>
      </Ingredients>
      <Preparation>
        <PreparationTitle>Preparation</PreparationTitle>
        <p>{recipe.instructions}</p>
      </Preparation>
    </PageContainer>
  );
};

export default RecipePage;
