import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom";
import axios from "axios"; // Make sure axios is configured for API calls

const PageContainer = styled.div`
  padding: 20px;
`;

const RecipeImage = styled.img`
  width: 100%;
  max-width: 600px;
  margin-bottom: 20px;
  border-radius: 5px;
`;

const RecipeTitle = styled.h1`
  margin-bottom: 20px;
`;

const RecipeContent = styled.div`
  font-size: 18px;
  line-height: 1.6;
`;

const BackButton = styled.a`
  display: inline-block;
  padding: 10px 20px;
  background-color: #fbf9f7;
  color: black;
  text-decoration: none;
  border: 1px solid #ccc;
  border-radius: 5px;
  margin-top: 20px;
  cursor: pointer;

  &:hover {
    background-color: #e1e1e1;
  }
`;

const RecipePage = () => {
  const [recipe, setRecipe] = useState(null);
  const { id } = useParams(); // Extract the recipe ID from the URL
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await axios.get(`/api/recipes/${id}`); // Adjust the URL as needed
        setRecipe(response.data);
      } catch (error) {
        console.error("Error fetching recipe", error);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipe();
  }, [id]);

  if (loading) return <div>Loading...</div>;

  if (!recipe) return <div>Recipe not found</div>;

  return (
    <PageContainer>
      <RecipeImage src={recipe.image} alt={recipe.title} />
      <RecipeTitle>{recipe.title}</RecipeTitle>
      <RecipeContent>
        <h3>Ingredients:</h3>
        <p>{recipe.ingredients}</p>
        <h3>Preparation:</h3>
        <p>{recipe.preparation}</p>
      </RecipeContent>
      <BackButton href="/recipes">Back to Recipes</BackButton>
    </PageContainer>
  );
};

export default RecipePage;
