import React, { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import styled from "styled-components";
import axios from "../services/axiosInstance";

const PageContainer = styled.div`
  padding: 20px;
  max-width: 800px;
  margin: 0 auto;
`;

const RecipeImage = styled.img`
  width: 100%;
  height: 300px;
  object-fit: cover;
  border-bottom: 1px solid #ddd;
  margin-bottom: 20px;
  border-radius: 8px;
`;

const RecipeTitle = styled.h1`
  margin-bottom: 20px;
  font-size: 2rem;
  color: #333;
`;

const Ingredients = styled.div`
  margin-bottom: 20px;
`;

const Preparation = styled.div`
  margin-bottom: 20px;
`;

const IngredientsTitle = styled.h2`
  font-size: 1.5rem;
  color: #555;
  margin-bottom: 10px;
`;

const PreparationTitle = styled.h2`
  font-size: 1.5rem;
  color: #555;
  margin-bottom: 10px;
`;

const EditButton = styled(Link)`
  padding: 10px 20px;
  background-color: #4caf50;
  color: white;
  text-decoration: none;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-right: 10px;

  &:hover {
    background-color: #45a049;
  }
`;

const DeleteButton = styled.button`
  padding: 10px 20px;
  background-color: #f44336;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #da190b;
  }
`;

const RecipePage = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await axios.get(`/recipes/${id}`);
        setRecipe(response.data);
      } catch (error) {
        setError("Error fetching recipe. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchRecipe();
  }, [id]);

  const handleDelete = async () => {
    try {
      await axios.delete(`/recipes/${id}`);
      navigate("/recipes");
    } catch (error) {
      console.error("Error deleting recipe", error);
      setError("Failed to delete the recipe.");
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!recipe) {
    return <div>Recipe not found</div>;
  }

  return (
    <PageContainer>
      <RecipeImage src={`/uploads/${recipe.image}`} alt={recipe.name} />
      <RecipeTitle>{recipe.name}</RecipeTitle>
      <Ingredients>
        <IngredientsTitle>Ingredients</IngredientsTitle>
        <p>{recipe.ingredients}</p>
      </Ingredients>
      <Preparation>
        <PreparationTitle>Preparation</PreparationTitle>
        <p>{recipe.preparation}</p>
      </Preparation>
      <EditButton to={`/edit-recipe/${recipe.id}`}>Edit</EditButton>
      <DeleteButton onClick={handleDelete}>Delete</DeleteButton>
    </PageContainer>
  );
};

export default RecipePage;
