import React, { useEffect, useState } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
} from "react-router-dom";
import axiosInstance from "./services/axiosInstance"; // Updated import
import Home from "./pages/Home";
import Register from "./pages/Register";
import RecipesPage from "./pages/RecipesPage";
import AddRecipePage from "./pages/AddRecipePage";
import Login from "./pages/Login";
import Navbar from "./components/Navbar";

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAuth = async () => {
      setLoading(true);
      try {
        // Try fetching a protected resource
        await axiosInstance.get("/recipes");
        setLoggedIn(true);
      } catch (error) {
        setLoggedIn(false);
      } finally {
        setLoading(false);
      }
    };

    checkAuth();
  }, []);

  // Redirect to login if still loading
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<Home isLoggedIn={loggedIn} />} />
        <Route path="/register" element={<Register />} />
        <Route
          path="/login"
          element={loggedIn ? <Navigate to="/recipes" /> : <Login />}
        />
        <Route
          path="/recipes"
          element={loggedIn ? <RecipesPage /> : <Navigate to="/login" />}
        />
        <Route
          path="/add-recipe"
          element={loggedIn ? <AddRecipePage /> : <Navigate to="/login" />}
        />
        {/* Other routes */}
      </Routes>
    </Router>
  );
};

export default App;
