import React from "react";
import styled from "styled-components";
import Navbar from "../components/Navbar";
import { Link } from "react-router-dom";
import RecipeCard from "../components/RecipeCard";
import recipesData from "../data";

const HomeContainer = styled.div`
  padding: 0;
  text-align: center;
  overflow-x: hidden;
  background-color: #faf3e0; /* Background color */
`;

const WelcomeBanner = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 50vh; /* Reduced height */
  background-color: #faf3e0; /* Soft beige */
  padding: 20px; /* Adjusted padding */
  color: #333333;
  text-align: center;
  margin-top: 80px; /* Reduced gap between features and welcome message */
`;

const WelcomeMessage = styled.h2`
  margin-bottom: 10px; /* Reduced margin */
  font-size: 2.5rem;
  font-weight: bold;
  color: #6a4c41; /* Warm brown color */

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const Description = styled.p`
  font-size: 1.2rem;
  margin-bottom: 15px; /* Reduced margin */
  color: #6a4c41; /* Warm brown color */
  max-width: 800px;
  margin: 0 auto;

  @media (max-width: 768px) {
    font-size: 1rem;
  }
`;

const CallToAction = styled.div`
  margin: 30px 0; /* Increased margin-top for more space */
`;

const CTAButton = styled(Link)`
  padding: 12px 24px;
  background-color: #ff6b6b; /* Soft red */
  color: white;
  text-decoration: none;
  border-radius: 25px;
  margin: 0 10px;
  font-size: 16px;
  font-weight: bold;
  transition: background-color 0.3s ease, transform 0.3s ease;

  &:hover {
    background-color: #ff4757; /* Darker red for hover effect */
    transform: translateY(-2px);
  }

  @media (max-width: 768px) {
    margin: 10px 0;
    font-size: 14px;
  }
`;

const FeatureHighlights = styled.div`
  display: flex;
  justify-content: center; /* Center align items */
  flex-wrap: wrap; /* Allow wrapping */
  margin: 20px auto; /* Reduced margin and centered */
  padding: 0;
  gap: 20px; /* Gap between items */
  max-width: 1200px; /* Max width to center align the features */
`;

const Feature = styled.div`
  width: calc(
    33.333% - 40px
  ); /* Adjust width for three items per row with gaps */
  text-align: center;
  background-color: #fffaf4; /* Very light beige */
  padding: 20px;
  border-radius: 10px; /* Reduced border radius */
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1); /* Softer shadow */
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  margin: 10px;

  &:hover {
    transform: translateY(-4px); /* Slight hover effect */
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2); /* Softer hover shadow */
  }

  @media (max-width: 1024px) {
    width: calc(50% - 40px); /* Two items per row for medium screens */
  }

  @media (max-width: 768px) {
    width: calc(100% - 40px); /* Full width for small screens */
  }
`;

const FeatureIcon = styled.div`
  font-size: 36px; /* Adjusted font size */
  margin-bottom: 10px; /* Reduced margin */
  color: #ff6b6b; /* Matching icon color */
`;

const FeatureText = styled.p`
  font-size: 16px;
  color: #6a4c41; /* Warm brown color */
  line-height: 1.5; /* Improved text readability */
`;

const SampleRecipes = styled.div`
  margin: 40px 20px;
  padding: 40px;
  background-color: #fffaf4; /* Same light beige as features */
  border-radius: 15px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);

  @media (max-width: 768px) {
    padding: 20px;
  }
`;

const Dish = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  gap: 20px;

  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const Home = ({ isLoggedIn, userName }) => {
  return (
    <HomeContainer>
      <Navbar isLoggedIn={isLoggedIn} />
      {isLoggedIn ? (
        <>
          <CallToAction>
            <CTAButton to="/add-recipe">Add New Recipe</CTAButton>
            <CTAButton to="/all-recipes">View All Recipes</CTAButton>
          </CallToAction>
        </>
      ) : (
        <>
          <WelcomeBanner>
            <WelcomeMessage>Welcome to Dish Diary!</WelcomeMessage>
            <Description>
              Save and manage your favorite recipes effortlessly.
            </Description>
            <CallToAction>
              <CTAButton to="/register">Sign Up</CTAButton>
              <CTAButton to="/login">Login</CTAButton>
            </CallToAction>
          </WelcomeBanner>
          <FeatureHighlights>
            <Feature>
              <FeatureIcon>üç≤</FeatureIcon>
              <FeatureText>Organize your recipes in one place.</FeatureText>
            </Feature>
            <Feature>
              <FeatureIcon>üìù</FeatureIcon>
              <FeatureText>Easily add, update, and delete recipes.</FeatureText>
            </Feature>
          </FeatureHighlights>
          <SampleRecipes>
            <h3>Sample Recipes</h3>
            <Dish>
              {recipesData.map((recipe) => (
                <RecipeCard key={recipe.id} recipe={recipe} />
              ))}
            </Dish>
          </SampleRecipes>
        </>
      )}
    </HomeContainer>
  );
};

export default Home;
