import React, { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const PageContainer = styled.div`
  padding: 20px;
  max-width: 800px;
  margin: 0 auto;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
`;

const InputField = styled.input`
  margin-bottom: 15px;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  font-size: 16px;
`;

const TextArea = styled.textarea`
  margin-bottom: 15px;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  font-size: 16px;
  min-height: 100px;
`;

const SubmitButton = styled.button`
  padding: 10px 20px;
  background-color: #fbf9f7;
  color: black;
  border: 1px solid #ccc;
  border-radius: 5px;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    background-color: #e1e1e1;
  }
`;

const Title = styled.h1`
  margin-bottom: 20px;
`;

const AddRecipePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [title, setTitle] = useState("");
  const [ingredients, setIngredients] = useState("");
  const [preparation, setPreparation] = useState("");
  const [image, setImage] = useState(null);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    if (id) {
      const fetchRecipe = async () => {
        try {
          const response = await axios.get(`/api/recipes/${id}`);
          const recipe = response.data;
          setTitle(recipe.title);
          setIngredients(recipe.ingredients);
          setPreparation(recipe.preparation);
          setImage(recipe.image);
        } catch (error) {
          console.error("Error fetching recipe", error);
        }
      };

      fetchRecipe();
    }
  }, [id]);

  const handleFileChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("ingredients", ingredients);
    formData.append("preparation", preparation);
    if (image) formData.append("image", image);

    try {
      if (id) {
        await axios.put(`/api/recipes/${id}`, formData, {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        });
      } else {
        await axios.post("/api/recipes", formData, {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        });
      }
      setSuccess(true);
      setTitle("");
      setIngredients("");
      setPreparation("");
      setImage(null);
      navigate("/recipes");
    } catch (error) {
      setError("Failed to add/update recipe. Please try again.");
    }
  };

  return (
    <PageContainer>
      <Title>{id ? "Edit Recipe" : "Add a New Recipe"}</Title>
      {success && (
        <div style={{ color: "green" }}>
          Recipe {id ? "updated" : "added"} successfully!
        </div>
      )}
      {error && <div style={{ color: "red" }}>{error}</div>}
      <Form onSubmit={handleSubmit}>
        <InputField
          type="text"
          placeholder="Recipe Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <InputField type="file" onChange={handleFileChange} required={!id} />
        <TextArea
          placeholder="Ingredients"
          value={ingredients}
          onChange={(e) => setIngredients(e.target.value)}
          required
        />
        <TextArea
          placeholder="Preparation Instructions"
          value={preparation}
          onChange={(e) => setPreparation(e.target.value)}
          required
        />
        <SubmitButton type="submit">
          {id ? "Update Recipe" : "Add Recipe"}
        </SubmitButton>
      </Form>
    </PageContainer>
  );
};

export default AddRecipePage;
