import React from "react";
import styled from "styled-components";
import Navbar from "../components/Navbar";
import { Link } from "react-router-dom";
import RecipeCard from "../components/RecipeCard";
import recipesData from "../data"; // Sample data

const HomeContainer = styled.div`
  padding: 0;
  text-align: center;
  overflow-x: hidden;
  background-color: #faf3e0;
`;

const WelcomeBanner = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 50vh;
  background-color: #faf3e0;
  padding: 20px;
  color: #333333;
  text-align: center;
  margin-top: 80px;
`;

const WelcomeMessage = styled.h2`
  margin-bottom: 10px;
  font-size: 2.5rem;
  font-weight: bold;
  color: #6a4c41;
`;

const Description = styled.p`
  font-size: 1.2rem;
  margin-bottom: 15px;
  color: #6a4c41;
  max-width: 800px;
  margin: 0 auto;
`;

const CallToAction = styled.div`
  margin: 30px 0;
`;

const CTAButton = styled(Link)`
  padding: 12px 24px;
  background-color: #ff6b6b;
  color: white;
  text-decoration: none;
  border-radius: 25px;
  margin: 0 10px;
  font-size: 16px;
  font-weight: bold;
  transition: background-color 0.3s ease, transform 0.3s ease;

  &:hover {
    background-color: #ff4757;
    transform: translateY(-2px);
  }
`;

const SampleRecipes = styled.div`
  margin: 40px 20px;
  padding: 40px;
  background-color: #fffaf4;
  border-radius: 15px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const Dish = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  gap: 20px;
`;

const Home = ({ isLoggedIn }) => {
  return (
    <HomeContainer>
      <Navbar isLoggedIn={isLoggedIn} />
      <WelcomeBanner>
        <WelcomeMessage>Welcome to Dish Diary!</WelcomeMessage>
        <Description>
          Save and manage your favorite recipes effortlessly.
        </Description>
        <CallToAction>
          <CTAButton to="/register">Sign Up</CTAButton>
          <CTAButton to="/login">Login</CTAButton>
        </CallToAction>
      </WelcomeBanner>

      <SampleRecipes>
        <h3>Sample Recipes</h3>
        <Dish>
          {recipesData.map((recipe) => (
            <RecipeCard key={recipe.id} recipe={recipe} />
          ))}
        </Dish>
      </SampleRecipes>
    </HomeContainer>
  );
};

export default Home;
