import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Navbar from "../components/Navbar";
import RecipeCard from "../components/RecipeCard"; // Import RecipeCard component
import axios from "../services/axiosInstance";
import { Link } from "react-router-dom";

const PageContainer = styled.div`
  padding: 20px;
`;

const NoRecipesMessage = styled.h2`
  text-align: center;
  margin-top: 50px;
`;

const Button = styled(Link)`
  padding: 10px 20px;
  background-color: #fbf9f7;
  color: black;
  text-decoration: none;
  border: 1px solid #ccc;
  border-radius: 5px;
  margin: 20px 0;
  display: block;
  text-align: center;

  &:hover {
    background-color: #e1e1e1;
  }
`;

const RecipesPage = () => {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchRecipes = async () => {
    try {
      const response = await axios.get("/recipes");
      setRecipes(response.data);
    } catch (error) {
      console.error("Error fetching recipes", error);
      alert(
        error.response?.data.message || "An error occurred. Please try again."
      );
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRecipes();
  }, []);

  const deleteRecipe = async (id) => {
    try {
      await axios.delete(`/recipes/${id}`);
      setRecipes(recipes.filter((recipe) => recipe.id !== id));
    } catch (error) {
      console.error("Error deleting recipe", error);
    }
  };

  if (loading) {
    return <PageContainer>Loading...</PageContainer>;
  }

  return (
    <PageContainer>
      <Navbar />
      {recipes.length === 0 ? (
        <NoRecipesMessage>No recipes found</NoRecipesMessage>
      ) : (
        recipes.map((recipe) => (
          <RecipeCard
            key={recipe.id}
            recipe={{
              id: recipe.id,
              name: recipe.title,
              image: `/uploads/${recipe.image}`, // Assuming `image` is the field name in your data
              ingredients: recipe.ingredients,
              preparation: recipe.preparation,
            }}
          />
        ))
      )}
      <Button to="/add-recipe">Add New Recipe</Button>
    </PageContainer>
  );
};

export default RecipesPage;
