import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { FaHeart } from "react-icons/fa";

const StyledNavbar = styled.nav`
  color: black;
  position: fixed; /* Fixes the Navbar to the top */
  top: 0;
  left: 0;
  right: 0;
  z-index: 1;
  display: flex;
  align-items: center; /* Center items vertically */
  width: 100%;
  padding: 0 20px; /* Padding to ensure spacing */
  box-sizing: border-box; /* Includes padding in width */
  overflow-x: hidden; /* Prevent horizontal scrolling */
  margin-top: 10px; /* Margin to create space from the top */
`;

const LogoLink = styled(Link)`
  text-decoration: none; /* Remove default underline */
  color: inherit; /* Inherit color from parent */
  flex: 1; /* Allow logo to take available space */
`;

const Logo = styled.h1`
  font-weight: bold;
  font-size: 30px;
  margin: 0;
  text-align: center;
`;

const MenuItem = styled.div`
  font-size: 15px;
  cursor: pointer;
  display: flex;
  align-items: center;
  margin-left: auto; /* Pushes the heart icon to the far right */
`;

const FavoritesIcon = styled(FaHeart)`
  color: red;
  font-size: 24px;
  cursor: pointer;
  margin-left: 20px; /* Add some margin to the left of the icon */
`;

const Navbar = () => {
  return (
    <StyledNavbar>
      <LogoLink to="/">
        <Logo>RECIPE TAKER</Logo>
      </LogoLink>
      <MenuItem>
        <StyledLink to="/favorites">
          <FavoritesIcon />
        </StyledLink>
      </MenuItem>
    </StyledNavbar>
  );
};

export default Navbar;
