import React, { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "../services/axiosInstance";
import Navbar from "../components/Navbar";
import { Link } from "react-router-dom";

const PageContainer = styled.div`
  padding: 20px;
`;

const NoRecipesMessage = styled.h2`
  text-align: center;
  margin-top: 50px;
`;

const RecipeCard = styled.div`
  display: inline-block;
  width: 30%;
  background: #fff;
  padding: 10px;
  margin: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  text-align: left;
`;

const RecipeImage = styled.img`
  width: 100%;
  border-bottom: 1px solid #ccc;
  margin-bottom: 10px;
`;

const RecipeTitle = styled.h3`
  margin: 0;
`;

const Button = styled(Link)`
  padding: 10px 20px;
  background-color: #fbf9f7;
  color: black;
  text-decoration: none;
  border: 1px solid #ccc;
  border-radius: 5px;
  margin: 20px 0;
  display: block;
  text-align: center;

  &:hover {
    background-color: #e1e1e1;
  }
`;

const EditButton = styled(Link)`
  padding: 5px 10px;
  background-color: #4caf50;
  color: white;
  text-decoration: none;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #45a049;
  }
`;

const DeleteButton = styled.button`
  padding: 5px 10px;
  background-color: #f44336;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #da190b;
  }
`;

const RecipesPage = () => {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchRecipes = async () => {
    try {
      const response = await axios.get("/recipes");
      setRecipes(response.data);
    } catch (error) {
      console.error("Error fetching recipes", error);
      if (!error.response) {
        alert(
          "Network error. Please check your internet connection or server status."
        );
      } else {
        alert(`Error: ${error.response.data.message || "An error occurred"}`);
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRecipes();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/recipes/${id}`);
      fetchRecipes(); // Refresh the list after deletion
    } catch (error) {
      console.error("Error deleting recipe", error);
      alert("Failed to delete recipe. Please try again.");
    }
  };

  return (
    <PageContainer>
      <Navbar />
      <Button to="/add-recipe">Add New Recipe</Button>
      {loading ? (
        <div>Loading...</div>
      ) : recipes.length === 0 ? (
        <NoRecipesMessage>
          No recipes found. Add some recipes to see them here.
        </NoRecipesMessage>
      ) : (
        <div>
          {recipes.map((recipe) => (
            <RecipeCard key={recipe.id}>
              {recipe.image && (
                <RecipeImage
                  src={`/uploads/${recipe.image}`}
                  alt={recipe.title}
                />
              )}
              <RecipeTitle>{recipe.title}</RecipeTitle>
              <p>
                <strong>Ingredients:</strong> {recipe.ingredients}
              </p>
              <p>
                <strong>Preparation:</strong> {recipe.preparation}
              </p>
              <EditButton to={`/add-recipe/${recipe.id}`}>Edit</EditButton>
              <DeleteButton onClick={() => handleDelete(recipe.id)}>
                Delete
              </DeleteButton>
            </RecipeCard>
          ))}
        </div>
      )}
    </PageContainer>
  );
};

export default RecipesPage;
