import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import recipesData from "../data"; // Import the static data

const PageContainer = styled.div`
  padding: 20px;
  max-width: 800px;
  margin: 0 auto;
`;

const RecipeImage = styled.img`
  width: 100%;
  height: 300px; /* Adjust as needed */
  object-fit: cover;
  border-bottom: 1px solid #ddd;
  margin-bottom: 20px;
  border-radius: 8px; /* Optional: for rounded corners */
`;

const RecipeTitle = styled.h1`
  margin-bottom: 20px;
  font-size: 2rem; /* Adjust as needed */
  color: #333;
`;

const Ingredients = styled.div`
  margin-bottom: 20px;
`;

const Preparation = styled.div`
  margin-bottom: 20px;
`;

const IngredientsTitle = styled.h2`
  font-size: 1.5rem;
  color: #555;
  margin-bottom: 10px;
`;

const PreparationTitle = styled.h2`
  font-size: 1.5rem;
  color: #555;
  margin-bottom: 10px;
`;

const PreparationList = styled.ul`
  list-style-type: decimal; /* Add numbers to the list */
  padding-left: 20px; /* Add padding for list items */
`;

const PreparationItem = styled.li`
  margin-bottom: 10px;
  font-size: 1rem; /* Adjust font size if needed */
`;

const RecipePage = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        setLoading(true);
        // Use static data for testing
        const recipeData = recipesData.find(
          (recipe) => recipe.id === parseInt(id)
        );
        if (recipeData) {
          setRecipe(recipeData);
        } else {
          setError("Recipe not found");
        }
      } catch (error) {
        setError("Error fetching recipe. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchRecipe();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!recipe) {
    return <div>Recipe not found</div>;
  }

  return (
    <PageContainer>
      <RecipeImage src={recipe.image} alt={recipe.name} />
      <RecipeTitle>{recipe.name}</RecipeTitle>
      <Ingredients>
        <IngredientsTitle>Ingredients</IngredientsTitle>
        <p>{recipe.ingredients}</p>
      </Ingredients>
      <Preparation>
        <PreparationTitle>Preparation</PreparationTitle>
        <PreparationList>
          {recipe.instructions.split("\n").map((step, index) => (
            <PreparationItem key={index}>{step}</PreparationItem>
          ))}
        </PreparationList>
      </Preparation>
    </PageContainer>
  );
};

export default RecipePage;
