import React, { useEffect, useState } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
} from "react-router-dom";
import axios from "./services/axiosInstance";
import Home from "./pages/Home";
import Register from "./pages/Register";
import RecipesPage from "./pages/RecipesPage";
import AddRecipePage from "./pages/AddRecipePage";
import Login from "./pages/Login";
import Navbar from "./components/Navbar";

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    const checkAuth = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setLoggedIn(false);
          return;
        }
        // Example: Check token validity or fetch user info
        await axios.get("/recipes"); // Or use a more secure endpoint
        setLoggedIn(true);
      } catch (error) {
        setLoggedIn(false);
      }
    };
    checkAuth();
  }, []);

  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/register" element={<Register />} />
        <Route
          path="/login"
          element={loggedIn ? <Navigate to="/recipes" /> : <Login />}
        />
        <Route
          path="/recipes"
          element={loggedIn ? <RecipesPage /> : <Navigate to="/login" />}
        />
        <Route
          path="/add-recipe"
          element={loggedIn ? <AddRecipePage /> : <Navigate to="/login" />}
        />
        {/* Other routes */}
      </Routes>
    </Router>
  );
};

export default App;
