import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { FaHeart } from "react-icons/fa";

const NavbarContainer = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
`;

const StyledNavbar = styled.nav`
  color: black;
  position: fixed; /* Fixes the Navbar to the top */
  top: 0;
  left: 0;
  right: 0;
  z-index: 3;
  display: flex;
  align-items: center; /* Center items vertically */
  width: 100%;
  background-color: #fbf9f7; /* Background color for contrast */
  padding: 0 20px; /* Padding to ensure spacing */
`;

const Logo = styled.h1`
  font-weight: bold;
  font-size: 30px;
  margin: 0;
  text-align: center;
  flex: 1; /* Allow logo to take available space */
`;

const MenuItem = styled.div`
  font-size: 15px;
  cursor: pointer;
  display: flex;
  align-items: center;
  margin-left: auto; /* Pushes the heart icon to the far right */
`;

const StyledLink = styled(Link)`
  color: inherit;
  text-decoration: none;
`;

const FavoritesIcon = styled(FaHeart)`
  color: red;
  font-size: 24px;
  cursor: pointer;
  margin-left: 20px; /* Add some margin to the left of the icon */
`;

const Navbar = () => {
  return (
    <NavbarContainer>
      <StyledNavbar>
        <Logo>RECIPE TAKER</Logo>
        <MenuItem>
          <StyledLink to="/favorites">
            <FavoritesIcon />
          </StyledLink>
        </MenuItem>
      </StyledNavbar>
    </NavbarContainer>
  );
};

export default Navbar;
