import React from "react";
import styled from "styled-components";
import Navbar from "../components/Navbar";
import { Link } from "react-router-dom";

const HomeContainer = styled.div`
  padding: 20px;
`;

const WelcomeMessage = styled.h2`
  text-align: center;
  margin-bottom: 20px;
`;

const FeaturedRecipe = styled.div`
  margin: 20px 0;
`;

const QuickAccess = styled.div`
  display: flex;
  justify-content: space-around;
  margin: 20px 0;
`;

const QuickButton = styled(Link)`
  padding: 10px 20px;
  background-color: #fbf9f7;
  color: black;
  text-decoration: none;
  border: 1px solid #ccc;
  border-radius: 5px;

  &:hover {
    background-color: #f1f1f1;
  }
`;

const RecentRecipes = styled.div`
  margin: 20px 0;
`;

const Home = ({ isLoggedIn, userName }) => {
  return (
    <div className="home-container">
      <Navbar isLoggedIn={isLoggedIn} userName={userName} />
      <HomeContainer>
        {isLoggedIn ? (
          <>
            <WelcomeMessage>Welcome back, {userName}!</WelcomeMessage>
            <FeaturedRecipe>
              <h3>Featured Recipe</h3>
              {/* Add code to display a featured recipe */}
            </FeaturedRecipe>
            <QuickAccess>
              <QuickButton to="/add-recipe">Add New Recipe</QuickButton>
              <QuickButton to="/all-recipes">View All Recipes</QuickButton>
            </QuickAccess>
            <RecentRecipes>
              <h3>Recent Recipes</h3>
              {/* Add code to display recent recipes */}
            </RecentRecipes>
          </>
        ) : (
          <WelcomeMessage>Welcome to Recipe Taker!</WelcomeMessage>
        )}
      </HomeContainer>
    </div>
  );
};

export default Home;
