import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import axios from "axios";
import Navbar from "../components/Navbar";

const PageContainer = styled.div`
  padding: 20px;
`;

const NoRecipesMessage = styled.h2`
  text-align: center;
  margin-top: 50px;
`;

const RecipeCard = styled.div`
  display: inline-block;
  width: 30%;
  background: #fff;
  padding: 10px;
  margin: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  text-align: left;
`;

const RecipeImage = styled.img`
  width: 100%;
  border-bottom: 1px solid #ccc;
  margin-bottom: 10px;
`;

const RecipeTitle = styled.h3`
  margin: 0;
`;

const Button = styled(Link)`
  padding: 10px 20px;
  background-color: #fbf9f7;
  color: black;
  text-decoration: none;
  border: 1px solid #ccc;
  border-radius: 5px;
  margin: 20px 0;
  display: block;
  text-align: center;

  &:hover {
    background-color: #e1e1e1;
  }
`;

const EditButton = styled(Link)`
  padding: 5px 10px;
  background-color: #4caf50;
  color: white;
  text-decoration: none;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #45a049;
  }
`;

const DeleteButton = styled.button`
  padding: 5px 10px;
  background-color: #f44336;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #da190b;
  }
`;

const RecipesPage = () => {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get("/api/recipes");
        setRecipes(response.data);
      } catch (error) {
        console.error("Error fetching recipes", error);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipes();
  }, []);

  const deleteRecipe = async (id) => {
    try {
      await axios.delete(`/api/recipes/${id}`);
      setRecipes(recipes.filter((recipe) => recipe.id !== id));
    } catch (error) {
      console.error("Error deleting recipe", error);
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <Navbar />
      <PageContainer>
        {recipes.length === 0 ? (
          <NoRecipesMessage>
            You haven't added any recipes yet. <br />
            <Button to="/add-recipe">Add Your First Recipe</Button>
          </NoRecipesMessage>
        ) : (
          <div>
            {recipes.map((recipe) => (
              <RecipeCard key={recipe.id}>
                <Link to={`/recipe/${recipe.id}`}>
                  <RecipeImage src={recipe.image} alt={recipe.title} />
                  <RecipeTitle>{recipe.title}</RecipeTitle>
                </Link>
                <EditButton to={`/edit-recipe/${recipe.id}`}>Edit</EditButton>
                <DeleteButton onClick={() => deleteRecipe(recipe.id)}>
                  Delete
                </DeleteButton>
              </RecipeCard>
            ))}
            <Button to="/add-recipe">Add New Recipe</Button>
          </div>
        )}
      </PageContainer>
    </div>
  );
};

export default RecipesPage;
